using Microsoft.Maui.Controls;

using QRCoder;

using System;

using ZXing.Net.Maui;

namespace Brandagent;

public partial class Page_QrCodeScanner : ContentPage
{
    public Data.Item Item { get; private set; }
    public bool ShouldClear { get; private set; }

    public Page_QrCodeScanner()
    {
        InitializeComponent();
    }

    void Reader_BarcodesDetected(object sender, BarcodeDetectionEventArgs e)
    {
        foreach (var barcode in e.Results)
        {
            if (barcode.Format != BarcodeFormat.QrCode)
                continue;

            var totp = barcode.Value;

            if (!totp.StartsWith("otpauth://totp/"))
                continue;

            var url = totp[15..].Split('?');

            var parameters = System.Web.HttpUtility.ParseQueryString(url[1]);

            var secret = parameters.Get("secret") ?? string.Empty;
            if (secret == string.Empty)
                continue;

            Item = new()
            {
                service = string.Empty,
                name = System.Web.HttpUtility.UrlDecode(url[0]),
                secret = secret,
                totp = totp,
                qr = "data:image/png;base64," + Convert.ToBase64String(
                    PngByteQRCodeHelper.GetQRCode(totp, QRCodeGenerator.ECCLevel.M, 6)
                )
            };

            Close();
        }
    }

    void CloseButton_Clicked(object sender, EventArgs e)
    {
        Close();
    }

    static void Close()
    {
        App.Current.MainPage.Navigation.PopModalAsync();
    }
    
    void AddButton_Clicked(object sender, EventArgs e)
    {
        Item = new()
        {
            service = "Bitwarden",
            name = "melektaus@outlook.at",
            secret = "QJJLQQSYNV5V2YNH5OPAOJDPJTPBOYLF",
            totp = "otpauth://totp/Bitwarden%3A%20melektaus%40outlook.at?secret=QJJLQQSYNV5V2YNH5OPAOJDPJTPBOYLF&digits=6&period=30",
            qr = "data:image/png;base64,"
        };

        Close();
    }

    void ClearButton_Clicked(object sender, EventArgs e)
    {
        ShouldClear = true;

        Close();
    }

}
